#!/bin/bash
# Utility functions library for system monitoring

# Color codes for output
readonly COLOR_RED='\033[0;31m'
readonly COLOR_GREEN='\033[0;32m'
readonly COLOR_YELLOW='\033[1;33m'
readonly COLOR_BLUE='\033[0;34m'
readonly COLOR_RESET='\033[0m'

# Logging functions
log_info() {
    echo -e "${COLOR_BLUE}[INFO]${COLOR_RESET} $(date '+%Y-%m-%d %H:%M:%S') - $*"
}

log_success() {
    echo -e "${COLOR_GREEN}[SUCCESS]${COLOR_RESET} $(date '+%Y-%m-%d %H:%M:%S') - $*"
}

log_warning() {
    echo -e "${COLOR_YELLOW}[WARNING]${COLOR_RESET} $(date '+%Y-%m-%d %H:%M:%S') - $*" >&2
}

log_error() {
    echo -e "${COLOR_RED}[ERROR]${COLOR_RESET} $(date '+%Y-%m-%d %H:%M:%S') - $*" >&2
}

# String manipulation functions
trim() {
    local var="$*"
    # Remove leading whitespace
    var="${var#"${var%%[![:space:]]*}"}"
    # Remove trailing whitespace
    var="${var%"${var##*[![:space:]]}"}"
    echo -n "$var"
}

to_upper() {
    echo "$1" | tr '[:lower:]' '[:upper:]'
}

to_lower() {
    echo "$1" | tr '[:upper:]' '[:lower:]'
}

# Validation functions
is_number() {
    [[ "$1" =~ ^[0-9]+$ ]]
}

is_empty() {
    [[ -z "$(trim "$1")" ]]
}

# File operations
ensure_directory() {
    local dir="$1"
    if [[ ! -d "$dir" ]]; then
        mkdir -p "$dir" || {
            log_error "Failed to create directory: $dir"
            return 1
        }
        log_info "Created directory: $dir"
    fi
}

backup_file() {
    local file="$1"
    local backup="${file}.backup.$(date +%Y%m%d_%H%M%S)"

    if [[ -f "$file" ]]; then
        cp "$file" "$backup" || {
            log_error "Failed to backup file: $file"
            return 1
        }
        log_info "Backed up file: $file -> $backup"
    fi
}

# Array utilities
join_array() {
    local delimiter="$1"
    shift
    local first="$1"
    shift
    printf '%s' "$first" "${@/#/$delimiter}"
}

# Convert bytes to human readable format
bytes_to_human() {
    local bytes="$1"
    local units=("B" "KB" "MB" "GB" "TB")
    local unit=0

    while (( $(echo "$bytes >= 1024" | bc -l) )) && (( unit < ${#units[@]} - 1 )); do
        bytes=$(echo "scale=2; $bytes / 1024" | bc)
        ((unit++))
    done

    printf "%.2f %s" "$bytes" "${units[$unit]}"
}

# Calculate percentage
calculate_percentage() {
    local part="$1"
    local total="$2"

    if (( $(echo "$total == 0" | bc -l) )); then
        echo "0.00"
    else
        printf "%.2f" "$(echo "scale=2; ($part / $total) * 100" | bc)"
    fi
}

# Error handling
set_error_trap() {
    trap 'log_error "Error occurred in ${BASH_SOURCE[0]} at line ${LINENO}"' ERR
}

# Cleanup function for trap
cleanup_on_exit() {
    local exit_code=$?
    if [[ $exit_code -ne 0 ]]; then
        log_error "Script exited with code: $exit_code"
    fi
    # Add any cleanup tasks here
}

echo "âœ“ lib/utils.sh validated and saved"
