// DSL definitions and builders for task automation
import groovy.transform.CompileStatic
import groovy.transform.TupleConstructor

// DSL Builder for creating tasks
class TaskDSL {
    String name
    String description
    List<String> dependencies = []
    Map<String, Object> config = [:]
    Closure action
    List<Closure> validators = []

    void name(String name) {
        this.name = name
    }

    void description(String desc) {
        this.description = desc
    }

    void dependsOn(String... deps) {
        dependencies.addAll(deps)
    }

    void configure(Map<String, Object> cfg) {
        config.putAll(cfg)
    }

    void validate(Closure validator) {
        validators << validator
    }

    void execute(Closure action) {
        this.action = action
    }

    // Method missing for dynamic configuration
    def methodMissing(String name, args) {
        if (args.length == 1) {
            config[name] = args[0]
        } else {
            config[name] = args
        }
    }
}

// Pipeline DSL Builder
class PipelineDSL {
    String name
    List<TaskDSL> tasks = []
    Map<String, Object> globalConfig = [:]
    List<Closure> beforeTasks = []
    List<Closure> afterTasks = []

    void pipeline(String name) {
        this.name = name
    }

    void globals(Map<String, Object> config) {
        globalConfig.putAll(config)
    }

    void beforeEach(Closure hook) {
        beforeTasks << hook
    }

    void afterEach(Closure hook) {
        afterTasks << hook
    }

    void task(String name, @DelegatesTo(TaskDSL) Closure closure) {
        def taskDsl = new TaskDSL()
        taskDsl.name = name
        closure.delegate = taskDsl
        closure.resolveStrategy = Closure.DELEGATE_FIRST
        closure()
        tasks << taskDsl
    }

    // Support for creating task groups using spread operator
    void taskGroup(String prefix, List<String> names, @DelegatesTo(TaskDSL) Closure template) {
        names.each { name ->
            task("${prefix}_${name}", template)
        }
    }
}

// DSL Entry point factory
class DSLFactory {
    static PipelineDSL createPipeline(@DelegatesTo(PipelineDSL) Closure closure) {
        def pipeline = new PipelineDSL()
        closure.delegate = pipeline
        closure.resolveStrategy = Closure.DELEGATE_FIRST
        closure()
        return pipeline
    }

    static TaskDSL createTask(@DelegatesTo(TaskDSL) Closure closure) {
        def task = new TaskDSL()
        closure.delegate = task
        closure.resolveStrategy = Closure.DELEGATE_FIRST
        closure()
        return task
    }
}

