// Task models and configurations for task automation system
import groovy.transform.*
import java.time.LocalDateTime
import java.time.Duration

// Task status enum
enum TaskStatus {
    PENDING, RUNNING, SUCCESS, FAILED, SKIPPED
}

// Task execution result
@TupleConstructor
@ToString(includeNames = true)
class TaskResult {
    TaskStatus status
    String output
    String error
    LocalDateTime startTime
    LocalDateTime endTime
    Map<String, Object> metadata = [:]

    // Using Groovy truth
    boolean isSuccess() {
        status == TaskStatus.SUCCESS
    }

    // Elvis operator for safe defaults
    Duration getDuration() {
        (startTime && endTime) ? Duration.between(startTime, endTime) : Duration.ZERO
    }

    // GString interpolation
    String getSummary() {
        "Task completed with status ${status} in ${duration.toMillis()}ms"
    }
}

// Task configuration
@Builder
@ToString(includeNames = true)
class TaskConfig {
    String name
    String description
    List<String> dependencies = []
    Map<String, Object> parameters = [:]
    int maxRetries = 0
    long timeoutMillis = 30000
    boolean parallel = false
    List<Closure> validators = []

    // Safe navigation operator example
    String getParameterValue(String key, String defaultValue = null) {
        parameters?.get(key)?.toString() ?: defaultValue
    }

    // Collection enhancement: findAll with closure
    List<String> getRequiredParameters() {
        parameters.findAll { k, v -> v == null }.collect { it.key }
    }
}

// Task definition
@CompileStatic
class Task {
    final String id
    final TaskConfig config
    final Closure action
    TaskStatus status = TaskStatus.PENDING
    TaskResult lastResult
    List<TaskResult> history = []

    Task(String id, TaskConfig config, Closure action) {
        this.id = id
        this.config = config
        this.action = action
    }

    // Using spread operator
    List<String> getAllDependencyIds() {
        config.dependencies*.toLowerCase()
    }

    void recordResult(TaskResult result) {
        lastResult = result
        history << result
        status = result.status
    }

    // Groovy truth and safe navigation
    boolean canRun() {
        status == TaskStatus.PENDING && config?.validators?.every { validator ->
            try {
                validator.call(config.parameters)
            } catch (Exception e) {
                false
            }
        }
    }
}

// Pipeline execution context
class ExecutionContext {
    final String pipelineId = UUID.randomUUID().toString()
    final Map<String, Task> tasks = [:]
    final Map<String, Object> globalVariables = [:]
    final List<String> executionOrder = []
    LocalDateTime startTime
    LocalDateTime endTime

    // Using GString and collection methods
    String getStatus() {
        def taskStatuses = tasks.values()*.status.groupBy { it }
        "Pipeline ${pipelineId}: ${taskStatuses.collect { status, list -> "${status}=${list.size()}" }.join(', ')}"
    }

    // Safe navigation and Elvis operator
    Task getTask(String taskId) {
        tasks[taskId] ?: null
    }

    // Spread operator to get all results
    List<TaskResult> getAllResults() {
        tasks.values()*.lastResult.findAll { it != null }
    }

    // Using Groovy collections and closures
    Map<TaskStatus, List<Task>> getTasksByStatus() {
        tasks.values().groupBy { it.status }
    }

    boolean isComplete() {
        tasks.every { id, task -> 
            task.status in [TaskStatus.SUCCESS, TaskStatus.FAILED, TaskStatus.SKIPPED]
        }
    }
}

// Pipeline statistics
@Canonical
class PipelineStats {
    int totalTasks
    int successfulTasks
    int failedTasks
    int skippedTasks
    Duration totalDuration
    Map<String, Duration> taskDurations = [:]

    static PipelineStats fromContext(ExecutionContext context) {
        def stats = new PipelineStats()
        def tasksByStatus = context.getTasksByStatus()

        stats.totalTasks = context.tasks.size()
        stats.successfulTasks = tasksByStatus[TaskStatus.SUCCESS]?.size() ?: 0
        stats.failedTasks = tasksByStatus[TaskStatus.FAILED]?.size() ?: 0
        stats.skippedTasks = tasksByStatus[TaskStatus.SKIPPED]?.size() ?: 0

        // Calculate total duration with safe navigation
        if (context.startTime && context.endTime) {
            stats.totalDuration = Duration.between(context.startTime, context.endTime)
        }

        // Collect task durations using collectEntries
        stats.taskDurations = context.tasks.collectEntries { id, task ->
            [(id): task.lastResult?.duration ?: Duration.ZERO]
        }

        return stats
    }

    // GString with formatting
    String getSummary() {
        """Pipeline Statistics:
        |  Total Tasks: ${totalTasks}
        |  Successful: ${successfulTasks}
        |  Failed: ${failedTasks}
        |  Skipped: ${skippedTasks}
        |  Total Duration: ${totalDuration?.toMillis() ?: 0}ms
        |  Average Task Duration: ${getAverageDuration()}ms
        """.stripMargin()
    }

    long getAverageDuration() {
        def durations = taskDurations.values().findAll { it.toMillis() > 0 }
        durations ? (durations*.toMillis().sum() / durations.size()) as long : 0
    }
}

