// Task executor service for running pipeline tasks
import java.util.concurrent.*
import java.time.LocalDateTime

class TaskExecutor {
    private final ExecutorService executorService
    private final Map<String, List<Closure>> hooks = [:]

    TaskExecutor(int threadPoolSize = 4) {
        this.executorService = Executors.newFixedThreadPool(threadPoolSize)
        setupDefaultHooks()
    }

    private void setupDefaultHooks() {
        // Default logging hooks using closures
        hooks['beforeTask'] = [
            { task -> println "[${LocalDateTime.now()}] Starting task: ${task.config.name}" }
        ]
        hooks['afterTask'] = [
            { task, result -> println "[${LocalDateTime.now()}] Completed task: ${task.config.name} - ${result.status}" }
        ]
        hooks['onError'] = [
            { task, error -> println "[${LocalDateTime.now()}] Error in task ${task.config.name}: ${error.message}" }
        ]
    }

    void addHook(String event, Closure hook) {
        hooks[event] = hooks[event] ?: []
        hooks[event] << hook
    }

    // Execute a single task
    TaskResult executeTask(Task task, ExecutionContext context) {
        // Run before hooks
        hooks['beforeTask']?.each { it.call(task) }

        def result = new TaskResult(
            startTime: LocalDateTime.now(),
            status: TaskStatus.RUNNING,
            metadata: [:]
        )

        try {
            // Check if task can run (validators)
            if (!task.canRun()) {
                result.status = TaskStatus.FAILED
                result.error = "Task validation failed"
                result.endTime = LocalDateTime.now()
                return result
            }

            // Check dependencies using Groovy collections
            def failedDeps = task.getAllDependencyIds().findAll { depId ->
                def dep = context.getTask(depId)
                dep?.status != TaskStatus.SUCCESS
            }

            if (failedDeps) {
                result.status = TaskStatus.SKIPPED
                result.output = "Skipped due to failed dependencies: ${failedDeps.join(', ')}"
                result.endTime = LocalDateTime.now()
                return result
            }

            // Execute task action with timeout
            def future = executorService.submit({
                // Create task execution environment
                def env = createTaskEnvironment(task, context)

                // Execute the task closure
                def output = task.action.call(env)
                return output
            } as Callable)

            // Wait for completion with timeout
            def output = future.get(task.config.timeoutMillis, TimeUnit.MILLISECONDS)

            result.status = TaskStatus.SUCCESS
            result.output = output?.toString() ?: "Task completed successfully"
            result.metadata['executionThread'] = Thread.currentThread().name

        } catch (TimeoutException e) {
            result.status = TaskStatus.FAILED
            result.error = "Task timed out after ${task.config.timeoutMillis}ms"
            hooks['onError']?.each { it.call(task, e) }
        } catch (Exception e) {
            result.status = TaskStatus.FAILED
            result.error = "Task failed: ${e.message}"
            result.metadata['exceptionClass'] = e.class.simpleName
            hooks['onError']?.each { it.call(task, e) }
        } finally {
            result.endTime = LocalDateTime.now()
            task.recordResult(result)

            // Run after hooks
            hooks['afterTask']?.each { it.call(task, result) }
        }

        return result
    }

    // Create task execution environment with access to context
    private Map<String, Object> createTaskEnvironment(Task task, ExecutionContext context) {
        [
            // Task parameters
            params: task.config.parameters,

            // Global variables
            globals: context.globalVariables,

            // Utility methods using closures
            log: { message -> println "[${task.config.name}] ${message}" },

            // Access to other task results using safe navigation
            getTaskResult: { taskId -> context.getTask(taskId)?.lastResult },

            // Set global variable
            setGlobal: { key, value -> context.globalVariables[key] = value },

            // Get global variable with Elvis operator
            getGlobal: { key, defaultValue = null -> 
                context.globalVariables[key] ?: defaultValue 
            },

            // Task metadata
            taskId: task.id,
            taskName: task.config.name
        ]
    }

    // Execute pipeline with dependency resolution
    PipelineStats executePipeline(ExecutionContext context) {
        context.startTime = LocalDateTime.now()

        try {
            // Build dependency graph and determine execution order
            def executionPlan = buildExecutionPlan(context.tasks)
            context.executionOrder.addAll(executionPlan)

            println "\nExecution plan: ${executionPlan.join(' -> ')}"

            // Group tasks by dependency level for parallel execution
            def taskLevels = groupTasksByLevel(context.tasks, executionPlan)

            // Execute tasks level by level
            taskLevels.each { level, taskIds ->
                println "\nExecuting level ${level}: ${taskIds.join(', ')}"

                if (taskIds.size() == 1) {
                    // Single task, execute directly
                    def task = context.getTask(taskIds[0])
                    executeTask(task, context)
                } else {
                    // Multiple tasks, execute in parallel if allowed
                    def futures = taskIds.collect { taskId ->
                        def task = context.getTask(taskId)
                        if (task.config.parallel) {
                            executorService.submit({
                                executeTask(task, context)
                            } as Callable<TaskResult>)
                        } else {
                            // Execute sequentially
                            CompletableFuture.completedFuture(executeTask(task, context))
                        }
                    }

                    // Wait for all tasks in this level to complete
                    futures.each { it.get() }
                }
            }

        } finally {
            context.endTime = LocalDateTime.now()
        }

        return PipelineStats.fromContext(context)
    }

    // Build execution plan using topological sort
    private List<String> buildExecutionPlan(Map<String, Task> tasks) {
        def visited = [:]
        def result = []

        // Closure for DFS with Groovy's each
        def visit
        visit = { taskId ->
            if (visited[taskId]) return
            visited[taskId] = true

            def task = tasks[taskId]
            task?.getAllDependencyIds()?.each { depId ->
                if (tasks.containsKey(depId)) {
                    visit(depId)
                }
            }
            result << taskId
        }

        // Visit all tasks
        tasks.keySet().each { visit(it) }

        return result
    }

    // Group tasks by execution level for parallel execution
    private Map<Integer, List<String>> groupTasksByLevel(Map<String, Task> tasks, List<String> executionPlan) {
        def levels = [:]
        def taskLevels = [:]

        executionPlan.each { taskId ->
            def task = tasks[taskId]
            def deps = task.getAllDependencyIds().findAll { tasks.containsKey(it) }

            // Find the maximum level of dependencies
            def maxDepLevel = deps.collect { taskLevels[it] ?: 0 }.max() ?: 0
            def level = maxDepLevel + (deps ? 1 : 0)

            taskLevels[taskId] = level
            levels[level] = levels[level] ?: []
            levels[level] << taskId
        }

        return levels.sort()
    }

    void shutdown() {
        executorService.shutdown()
        try {
            if (!executorService.awaitTermination(60, TimeUnit.SECONDS)) {
                executorService.shutdownNow()
            }
        } catch (InterruptedException e) {
            executorService.shutdownNow()
        }
    }
}

