    // Global configuration
    globals(
        projectName: 'AwesomeApp',
        version: '1.0.0',
        buildDir: '/tmp/build'
    )

    // Before each task hook
    beforeEach { task ->
        println "[HOOK] Preparing to run: ${task.config.name}"
