        }
    }

    // Define compile task
    task('compile') {
        description 'Compile source code'
        dependsOn 'clean'

        // Dynamic configuration using methodMissing
        compiler 'groovyc'
        sourceDir 'src/main/groovy'
        optimization true

        validate { params ->
            params.compiler != null
