        execute { env ->
            def compiler = env.params.compiler
            env.log "Compiling with ${compiler}..."
            Thread.sleep(1000) // Simulate compilation
            env.setGlobal('compiledClasses', 150)
            "Compiled 150 classes successfully"
        }
    }

    // Define test task
    task('test') {
        description 'Run unit tests'
        dependsOn 'compile'
        parallel false

        // Configuration using map
