            ]

            env.setGlobal('testResults', testResults)

            // Using GString and Groovy truth
            if (testResults.failed) {
                throw new RuntimeException("${testResults.failed} tests failed!")
            }

            "All ${testResults.passed} tests passed!"
        }
    }

    // Define package task
    task('package') {
        description 'Package application'
        dependsOn 'compile'  // Note: doesn't depend on test

        execute { env ->
            def version = env.getGlobal('version')
            def projectName = env.getGlobal('projectName')

            env.log "Creating ${projectName}-${version}.jar"
            Thread.sleep(800)

            def jarFile = "${projectName}-${version}.jar"
            env.setGlobal('artifact', jarFile)

            "Created ${jarFile} (2.3 MB)"
        }
