        description { "Run ${delegate.name} validation" }
        dependsOn 'compile'
        parallel true
        timeoutMillis 3000

        execute { env ->
            def taskType = env.taskName.split('_')[1]
            env.log "Running ${taskType} analysis..."
            Thread.sleep(700)
            "${taskType} found ${(Math.random() * 10).toInteger()} issues"
        }
    }

    // Deploy task
    task('deploy') {
        description 'Deploy to staging'
        dependsOn 'package', 'test', 'validate_checkstyle', 'validate_findbugs'

        // Deployment configuration
        server 'staging.example.com'
        deployPath '/opt/apps'

        validate { params ->
            // Using safe navigation and Elvis operator
