        }

        execute { env ->
            def artifact = env.getGlobal('artifact', 'unknown.jar')
            def server = env.params.server

            env.log "Deploying ${artifact} to ${server}"
            Thread.sleep(2000)

            "Successfully deployed to ${server}"
        }
    }
}

