package models;

import java.time.LocalDateTime;
import java.util.*;
import java.util.stream.Collectors;

/**
 * Order entity with order items and status tracking
 */
public class Order {
    private Long id;
    private String customerEmail;
    private OrderStatus status;
    private List<OrderItem> items;
    private LocalDateTime createdAt;
    private LocalDateTime completedAt;

    /**
     * Order status enum
     */
    public enum OrderStatus {
        PENDING("Pending"),
        PROCESSING("Processing"),
        COMPLETED("Completed"),
        CANCELLED("Cancelled"),
        REFUNDED("Refunded");

        private final String displayName;

        OrderStatus(String displayName) {
            this.displayName = displayName;
        }

        public String getDisplayName() {
            return displayName;
        }

        public boolean isActive() {
            return this == PENDING || this == PROCESSING;
        }
    }

    /**
     * Order item nested class
     */
    public static class OrderItem {
        private Long productId;
        private String productName;
        private double unitPrice;
        private int quantity;

        public OrderItem(Long productId, String productName, double unitPrice, int quantity) {
            this.productId = productId;
            this.productName = productName;
            this.unitPrice = unitPrice;
            this.quantity = quantity;
        }

        public double getSubtotal() {
            return unitPrice * quantity;
        }

        // Getters
        public Long getProductId() { return productId; }
        public String getProductName() { return productName; }
        public double getUnitPrice() { return unitPrice; }
        public int getQuantity() { return quantity; }

        @Override
        public String toString() {
            return String.format("%s x%d @ $%.2f = $%.2f", 
                    productName, quantity, unitPrice, getSubtotal());
        }
    }

    // Constructor
    public Order(Long id, String customerEmail) {
        this.id = id;
        this.customerEmail = customerEmail;
        this.status = OrderStatus.PENDING;
        this.items = new ArrayList<>();
        this.createdAt = LocalDateTime.now();
    }

    // Business methods
    public void addItem(Product product, int quantity) {
        // Check if item already exists
        Optional<OrderItem> existingItem = items.stream()
                .filter(item -> item.getProductId().equals(product.getId()))
                .findFirst();

        if (existingItem.isPresent()) {
            // Update quantity of existing item
            OrderItem item = existingItem.get();
            items.remove(item);
            items.add(new OrderItem(product.getId(), product.getName(), 
                    product.getPrice(), item.getQuantity() + quantity));
        } else {
            // Add new item
            items.add(new OrderItem(product.getId(), product.getName(), 
                    product.getPrice(), quantity));
        }
    }

    public boolean removeItem(Long productId) {
        return items.removeIf(item -> item.getProductId().equals(productId));
    }

    public double calculateTotal() {
        return items.stream()
                .mapToDouble(OrderItem::getSubtotal)
                .sum();
    }

    public Map<Long, Integer> getProductQuantities() {
        return items.stream()
                .collect(Collectors.toMap(
                    OrderItem::getProductId,
                    OrderItem::getQuantity
                ));
    }

    public void complete() {
        if (status != OrderStatus.PENDING && status != OrderStatus.PROCESSING) {
            throw new IllegalStateException("Cannot complete order in status: " + status);
        }
        this.status = OrderStatus.COMPLETED;
        this.completedAt = LocalDateTime.now();
    }

    public void cancel() {
        if (!status.isActive()) {
            throw new IllegalStateException("Cannot cancel order in status: " + status);
        }
        this.status = OrderStatus.CANCELLED;
    }

    // Getters and setters
    public Long getId() { return id; }
    public void setId(Long id) { this.id = id; }

    public String getCustomerEmail() { return customerEmail; }
    public void setCustomerEmail(String customerEmail) { this.customerEmail = customerEmail; }

    public OrderStatus getStatus() { return status; }
    public void setStatus(OrderStatus status) { this.status = status; }

    public List<OrderItem> getItems() { return new ArrayList<>(items); }

    public LocalDateTime getCreatedAt() { return createdAt; }
    public LocalDateTime getCompletedAt() { return completedAt; }

    @Override
    public String toString() {
        return String.format("Order[id=%d, customer='%s', status=%s, items=%d, total=$%.2f]",
                id, customerEmail, status.getDisplayName(), items.size(), calculateTotal());
    }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (o == null || getClass() != o.getClass()) return false;
        Order order = (Order) o;
        return Objects.equals(id, order.id);
    }

    @Override
    public int hashCode() {
        return Objects.hash(id);
    }
}

