package services;

import interfaces.Repository;
import interfaces.Service;
import models.Product;
import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.atomic.AtomicLong;
import java.util.stream.Collectors;

/**
 * Product service implementation with in-memory repository
 */
public class ProductService implements Service.ProductService, Repository<Product, Long> {
    private final Map<Long, Product> products = new ConcurrentHashMap<>();
    private final AtomicLong idGenerator = new AtomicLong(1);

    // Repository implementation
    @Override
    public Product save(Product entity) {
        if (entity.getId() == null) {
            entity.setId(idGenerator.getAndIncrement());
        }
        products.put(entity.getId(), entity);
        return entity;
    }

    @Override
    public Optional<Product> findById(Long id) {
        return Optional.ofNullable(products.get(id));
    }

    @Override
    public List<Product> findAll() {
        return new ArrayList<>(products.values());
    }

    @Override
    public boolean deleteById(Long id) {
        return products.remove(id) != null;
    }

    @Override
    public long count() {
        return products.size();
    }

    // ProductService implementation
    @Override
    public Product createProduct(String name, String description, double price, int quantity) {
        if (name == null || name.trim().isEmpty()) {
            throw new IllegalArgumentException("Product name cannot be empty");
        }
        if (price < 0) {
            throw new IllegalArgumentException("Product price cannot be negative");
        }
        if (quantity < 0) {
            throw new IllegalArgumentException("Product quantity cannot be negative");
        }

        Product product = new Product.Builder()
                .name(name)
                .description(description)
                .price(price)
                .stockQuantity(quantity)
                .build();

        return save(product);
    }

    @Override
    public Optional<Product> getProduct(Long id) {
        return findById(id);
    }

    @Override
    public List<Product> getAllProducts() {
        return findAll();
    }

    @Override
    public List<Product> getProductsInStock() {
        return products.values().stream()
                .filter(Product::isInStock)
                .sorted(Comparator.comparing(Product::getName))
                .collect(Collectors.toList());
    }

    @Override
    public boolean updateStock(Long productId, int quantity) {
        Optional<Product> productOpt = findById(productId);
        if (productOpt.isPresent()) {
            Product product = productOpt.get();
            product.setStockQuantity(quantity);
            save(product);
            return true;
        }
        return false;
    }

    @Override
    public boolean deleteProduct(Long id) {
        return deleteById(id);
    }

    @Override
    public List<Product> searchProducts(String keyword) {
        if (keyword == null || keyword.trim().isEmpty()) {
            return findAll();
        }

        String lowerKeyword = keyword.toLowerCase();
        return products.values().stream()
                .filter(p -> p.getName().toLowerCase().contains(lowerKeyword) ||
                        (p.getDescription() != null && 
                         p.getDescription().toLowerCase().contains(lowerKeyword)))
                .sorted(Comparator.comparing(Product::getName))
                .collect(Collectors.toList());
    }

    // Additional business methods
    public List<Product> getProductsByPriceRange(double minPrice, double maxPrice) {
        return products.values().stream()
                .filter(p -> p.getPrice() >= minPrice && p.getPrice() <= maxPrice)
                .sorted(Comparator.comparing(Product::getPrice))
                .collect(Collectors.toList());
    }

    public Map<Product.ProductStatus, Long> getProductCountByStatus() {
        return products.values().stream()
                .collect(Collectors.groupingBy(
                    Product::getStatus,
                    Collectors.counting()
                ));
    }

    public Optional<Product> getMostExpensiveProduct() {
        return products.values().stream()
                .max(Comparator.comparing(Product::getPrice));
    }

    public double getTotalInventoryValue() {
        return products.values().stream()
                .mapToDouble(p -> p.getPrice() * p.getStockQuantity())
                .sum();
    }
}

