package services;

import interfaces.Repository;
import interfaces.Service;
import models.Order;
import models.Product;
import java.util.*;
import java.util.concurrent.ConcurrentHashMap;
import java.util.concurrent.atomic.AtomicLong;
import java.util.stream.Collectors;

/**
 * Order service implementation with inventory management
 */
public class OrderService implements Service.OrderService, Service.InventoryService, Repository<Order, Long> {
    private final Map<Long, Order> orders = new ConcurrentHashMap<>();
    private final AtomicLong idGenerator = new AtomicLong(1);
    private final ProductService productService;

    // Constructor
    public OrderService(ProductService productService) {
        this.productService = productService;
    }

    // Repository implementation
    @Override
    public Order save(Order entity) {
        if (entity.getId() == null) {
            entity.setId(idGenerator.getAndIncrement());
        }
        orders.put(entity.getId(), entity);
        return entity;
    }

    @Override
    public Optional<Order> findById(Long id) {
        return Optional.ofNullable(orders.get(id));
    }

    @Override
    public List<Order> findAll() {
        return new ArrayList<>(orders.values());
    }

    @Override
    public boolean deleteById(Long id) {
        return orders.remove(id) != null;
    }

    @Override
    public long count() {
        return orders.size();
    }

    // OrderService implementation
    @Override
    public Order createOrder(String customerEmail) {
        if (customerEmail == null || !customerEmail.contains("@")) {
            throw new IllegalArgumentException("Invalid customer email");
        }

        Order order = new Order(null, customerEmail);
        return save(order);
    }

    @Override
    public Optional<Order> getOrder(Long id) {
        return findById(id);
    }

    @Override
    public List<Order> getAllOrders() {
        return findAll();
    }

    @Override
    public List<Order> getOrdersByCustomer(String customerEmail) {
        return orders.values().stream()
                .filter(o -> o.getCustomerEmail().equalsIgnoreCase(customerEmail))
                .sorted(Comparator.comparing(Order::getCreatedAt).reversed())
                .collect(Collectors.toList());
    }

    @Override
    public boolean addItemToOrder(Long orderId, Long productId, int quantity) throws Exception {
        Optional<Order> orderOpt = findById(orderId);
        Optional<Product> productOpt = productService.findById(productId);

        if (!orderOpt.isPresent()) {
            throw new NoSuchElementException("Order not found: " + orderId);
        }
        if (!productOpt.isPresent()) {
            throw new NoSuchElementException("Product not found: " + productId);
        }

        Order order = orderOpt.get();
        Product product = productOpt.get();

        if (!order.getStatus().isActive()) {
            throw new IllegalStateException("Cannot modify order in status: " + order.getStatus());
        }

        if (!checkAvailability(productId, quantity)) {
            throw new IllegalStateException("Insufficient stock for product: " + product.getName());
        }

        // Reserve the stock
        reserveStock(productId, quantity);

        // Add item to order
        order.addItem(product, quantity);
        save(order);

        return true;
    }

    @Override
    public boolean completeOrder(Long orderId) throws Exception {
        Optional<Order> orderOpt = findById(orderId);
        if (!orderOpt.isPresent()) {
            throw new NoSuchElementException("Order not found: " + orderId);
        }

        Order order = orderOpt.get();
        if (order.getItems().isEmpty()) {
            throw new IllegalStateException("Cannot complete empty order");
        }

        order.complete();
        save(order);

        return true;
    }

    @Override
    public boolean cancelOrder(Long orderId) throws Exception {
        Optional<Order> orderOpt = findById(orderId);
        if (!orderOpt.isPresent()) {
            throw new NoSuchElementException("Order not found: " + orderId);
        }

        Order order = orderOpt.get();

        // Release reserved stock
        for (Order.OrderItem item : order.getItems()) {
            releaseStock(item.getProductId(), item.getQuantity());
        }

        order.cancel();
        save(order);

        return true;
    }

    @Override
    public double calculateOrderTotal(Long orderId) {
        return findById(orderId)
                .map(Order::calculateTotal)
                .orElse(0.0);
    }

    // InventoryService implementation
    @Override
    public boolean checkAvailability(Long productId, int quantity) {
        return productService.findById(productId)
                .map(p -> p.getStockQuantity() >= quantity)
                .orElse(false);
    }

    @Override
    public void reserveStock(Long productId, int quantity) throws Exception {
        Optional<Product> productOpt = productService.findById(productId);
        if (!productOpt.isPresent()) {
            throw new NoSuchElementException("Product not found: " + productId);
        }

        Product product = productOpt.get();
        if (product.getStockQuantity() < quantity) {
            throw new IllegalStateException("Insufficient stock");
        }

        product.adjustStock(-quantity);
        productService.save(product);
    }

    @Override
    public void releaseStock(Long productId, int quantity) {
        productService.findById(productId).ifPresent(product -> {
            product.adjustStock(quantity);
            productService.save(product);
        });
    }

    @Override
    public List<Product> getLowStockProducts(int threshold) {
        return productService.findAll().stream()
                .filter(p -> p.getStockQuantity() < threshold && p.getStockQuantity() > 0)
                .sorted(Comparator.comparing(Product::getStockQuantity))
                .collect(Collectors.toList());
    }

    // Additional business methods
    public Map<Order.OrderStatus, Long> getOrderCountByStatus() {
        return orders.values().stream()
                .collect(Collectors.groupingBy(
                    Order::getStatus,
                    Collectors.counting()
                ));
    }

    public List<Order> getActiveOrders() {
        return orders.values().stream()
                .filter(o -> o.getStatus().isActive())
                .sorted(Comparator.comparing(Order::getCreatedAt))
                .collect(Collectors.toList());
    }

    public double getTotalRevenue() {
        return orders.values().stream()
                .filter(o -> o.getStatus() == Order.OrderStatus.COMPLETED)
                .mapToDouble(Order::calculateTotal)
                .sum();
    }

    public Map<String, Double> getRevenueByCustomer() {
        return orders.values().stream()
                .filter(o -> o.getStatus() == Order.OrderStatus.COMPLETED)
                .collect(Collectors.groupingBy(
                    Order::getCustomerEmail,
                    Collectors.summingDouble(Order::calculateTotal)
                ));
    }
}

