// Utility functions for our Node.js microservice
const crypto = require('crypto');

class Logger {
    constructor(service) {
        this.service = service;
    }

    info(message) {
        console.log(`[${new Date().toISOString()}] ${this.service} INFO: ${message}`);
    }

    error(message) {
        console.log(`[${new Date().toISOString()}] ${this.service} ERROR: ${message}`);
    }

    debug(message) {
        console.log(`[${new Date().toISOString()}] ${this.service} DEBUG: ${message}`);
    }
}

function generateId() {
    return crypto.randomBytes(16).toString('hex');
}

function validateEmail(email) {
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    return emailRegex.test(email);
}

function sanitizeString(str, maxLength = 1000) {
    if (typeof str !== 'string') return '';
    return str.trim().substring(0, maxLength);
}

function createResponse(data, status = 'success') {
    return {
        status,
        timestamp: new Date().toISOString(),
        data
    };
}

function createErrorResponse(message, code = 'error') {
    return createResponse({ error: message, code }, 'error');
}

function asyncDelay(ms) {
    return new Promise(resolve => setTimeout(resolve, ms));
}

class RateLimiter {
    constructor(maxRequests = 10, windowMs = 60000) {
        this.maxRequests = maxRequests;
        this.windowMs = windowMs;
        this.requests = new Map();
    }

    isAllowed(identifier) {
        const now = Date.now();
        const userRequests = this.requests.get(identifier) || [];

        // Remove old requests outside the window
        const validRequests = userRequests.filter(time => now - time < this.windowMs);

        if (validRequests.length >= this.maxRequests) {
            return false;
        }

        validRequests.push(now);
        this.requests.set(identifier, validRequests);
        return true;
    }
}

module.exports = {
    Logger,
    generateId,
    validateEmail,
    sanitizeString,
    createResponse,
    createErrorResponse,
    asyncDelay,
    RateLimiter
};

