// In-memory database for our Node.js microservice
const { generateId, Logger } = require('./utils');

class MemoryDatabase {
    constructor() {
        this.users = new Map();
        this.posts = new Map();
        this.sessions = new Map();
        this.logger = new Logger('Database');
    }

    // User operations
    async createUser(userData) {
        const user = {
            id: generateId(),
            username: userData.username,
            email: userData.email,
            createdAt: new Date().toISOString(),
            active: true
        };

        this.users.set(user.id, user);
        this.logger.info(`User created: ${user.username} (${user.id})`);
        return user;
    }

    async getUserById(id) {
        return this.users.get(id) || null;
    }

    async getUserByUsername(username) {
        for (const user of this.users.values()) {
            if (user.username === username) {
                return user;
            }
        }
        return null;
    }

    async getAllUsers() {
        return Array.from(this.users.values());
    }

    async updateUser(id, updates) {
        const user = this.users.get(id);
        if (!user) return null;

        const updatedUser = { ...user, ...updates, id };
        this.users.set(id, updatedUser);
        this.logger.info(`User updated: ${id}`);
        return updatedUser;
    }

    async deleteUser(id) {
        const deleted = this.users.delete(id);
        if (deleted) {
            this.logger.info(`User deleted: ${id}`);
            // Also delete user's posts
            for (const [postId, post] of this.posts.entries()) {
                if (post.authorId === id) {
                    this.posts.delete(postId);
                }
            }
        }
        return deleted;
    }

    // Post operations
    async createPost(postData) {
        const post = {
            id: generateId(),
            title: postData.title,
            content: postData.content,
            authorId: postData.authorId,
            createdAt: new Date().toISOString(),
            published: postData.published || false,
            tags: postData.tags || []
        };

        this.posts.set(post.id, post);
        this.logger.info(`Post created: ${post.title} (${post.id})`);
        return post;
    }

    async getPostById(id) {
        return this.posts.get(id) || null;
    }

    async getPostsByAuthor(authorId) {
        const posts = [];
        for (const post of this.posts.values()) {
            if (post.authorId === authorId) {
                posts.push(post);
            }
        }
        return posts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
    }

    async getAllPosts(published = null) {
        let posts = Array.from(this.posts.values());

        if (published !== null) {
            posts = posts.filter(post => post.published === published);
        }

        return posts.sort((a, b) => new Date(b.createdAt) - new Date(a.createdAt));
    }

    async updatePost(id, updates) {
        const post = this.posts.get(id);
        if (!post) return null;

        const updatedPost = { ...post, ...updates, id };
        this.posts.set(id, updatedPost);
        this.logger.info(`Post updated: ${id}`);
        return updatedPost;
    }

    async deletePost(id) {
        const deleted = this.posts.delete(id);
        if (deleted) {
            this.logger.info(`Post deleted: ${id}`);
        }
        return deleted;
    }

    // Session operations
    async createSession(userId) {
        const session = {
            id: generateId(),
            userId,
            createdAt: new Date().toISOString(),
            lastAccessed: new Date().toISOString()
        };

        this.sessions.set(session.id, session);
        this.logger.info(`Session created: ${session.id} for user ${userId}`);
        return session;
    }

    async getSession(sessionId) {
        const session = this.sessions.get(sessionId);
        if (session) {
            session.lastAccessed = new Date().toISOString();
            this.sessions.set(sessionId, session);
        }
        return session || null;
    }

    async deleteSession(sessionId) {
        const deleted = this.sessions.delete(sessionId);
        if (deleted) {
            this.logger.info(`Session deleted: ${sessionId}`);
        }
        return deleted;
    }

    // Stats
    getStats() {
        return {
            users: this.users.size,
            posts: this.posts.size,
            publishedPosts: Array.from(this.posts.values()).filter(p => p.published).length,
            sessions: this.sessions.size
        };
    }
}

module.exports = MemoryDatabase;

