// API routes for our Node.js microservice
const { Logger, validateEmail, sanitizeString, createResponse, createErrorResponse, RateLimiter } = require('./utils');
const MemoryDatabase = require('./database');

class UserAPI {
    constructor(database) {
        this.db = database;
        this.logger = new Logger('UserAPI');
        this.rateLimiter = new RateLimiter(5, 60000); // 5 requests per minute
    }

    async createUser(requestData, clientId = 'default') {
        try {
            if (!this.rateLimiter.isAllowed(clientId)) {
                return createErrorResponse('Rate limit exceeded', 'rate_limit');
            }

            const { username, email } = requestData;

            if (!username || !email) {
                return createErrorResponse('Username and email are required');
            }

            const cleanUsername = sanitizeString(username, 50);
            const cleanEmail = sanitizeString(email, 100);

            if (!validateEmail(cleanEmail)) {
                return createErrorResponse('Invalid email format');
            }

            // Check if username already exists
            const existingUser = await this.db.getUserByUsername(cleanUsername);
            if (existingUser) {
                return createErrorResponse('Username already exists', 'conflict');
            }

            const user = await this.db.createUser({
                username: cleanUsername,
                email: cleanEmail
            });

            this.logger.info(`User created: ${user.username}`);
            return createResponse(user);

        } catch (error) {
            this.logger.error(`Failed to create user: ${error.message}`);
            return createErrorResponse('Failed to create user');
        }
    }

    async getUser(userId) {
        try {
            const user = await this.db.getUserById(userId);
            if (!user) {
                return createErrorResponse('User not found', 'not_found');
            }
            return createResponse(user);
        } catch (error) {
            this.logger.error(`Failed to get user: ${error.message}`);
            return createErrorResponse('Failed to get user');
        }
    }

    async listUsers() {
        try {
            const users = await this.db.getAllUsers();
            return createResponse({ users, count: users.length });
        } catch (error) {
            this.logger.error(`Failed to list users: ${error.message}`);
            return createErrorResponse('Failed to list users');
        }
    }
}

class PostAPI {
    constructor(database) {
        this.db = database;
        this.logger = new Logger('PostAPI');
        this.rateLimiter = new RateLimiter(10, 60000); // 10 requests per minute
    }

    async createPost(requestData, clientId = 'default') {
        try {
            if (!this.rateLimiter.isAllowed(clientId)) {
                return createErrorResponse('Rate limit exceeded', 'rate_limit');
            }

            const { title, content, authorId, published, tags } = requestData;

            if (!title || !content || !authorId) {
                return createErrorResponse('Title, content, and authorId are required');
            }

            // Verify author exists
            const author = await this.db.getUserById(authorId);
            if (!author) {
                return createErrorResponse('Author not found', 'not_found');
            }

            const post = await this.db.createPost({
                title: sanitizeString(title, 200),
                content: sanitizeString(content, 10000),
                authorId,
                published: published || false,
                tags: Array.isArray(tags) ? tags.map(tag => sanitizeString(tag, 50)) : []
            });

            this.logger.info(`Post created: ${post.title}`);
            return createResponse(post);

        } catch (error) {
            this.logger.error(`Failed to create post: ${error.message}`);
            return createErrorResponse('Failed to create post');
        }
    }

    async getPost(postId) {
        try {
            const post = await this.db.getPostById(postId);
            if (!post) {
                return createErrorResponse('Post not found', 'not_found');
            }

            // Get author info
            const author = await this.db.getUserById(post.authorId);
            const enrichedPost = {
                ...post,
                author: author ? { username: author.username, id: author.id } : null
            };

            return createResponse(enrichedPost);
        } catch (error) {
            this.logger.error(`Failed to get post: ${error.message}`);
            return createErrorResponse('Failed to get post');
        }
    }

    async getUserPosts(authorId) {
        try {
            const posts = await this.db.getPostsByAuthor(authorId);
            return createResponse({ posts, count: posts.length });
        } catch (error) {
            this.logger.error(`Failed to get user posts: ${error.message}`);
            return createErrorResponse('Failed to get user posts');
        }
    }

    async listPosts(published = null) {
        try {
            const posts = await this.db.getAllPosts(published);
            return createResponse({ posts, count: posts.length });
        } catch (error) {
            this.logger.error(`Failed to list posts: ${error.message}`);
            return createErrorResponse('Failed to list posts');
        }
    }
}

module.exports = { UserAPI, PostAPI };

