const { Logger, asyncDelay } = require('./utils');
const MemoryDatabase = require('./database');
const { UserAPI, PostAPI } = require('./api');

class MicroService {
    constructor() {
        this.logger = new Logger('MicroService');
        this.database = new MemoryDatabase();
        this.userAPI = new UserAPI(this.database);
        this.postAPI = new PostAPI(this.database);
    }

    async start() {
        this.logger.info('Starting microservice...');

        // Simulate startup delay
        await asyncDelay(100);

        this.logger.info('Microservice started successfully');
        return this;
    }

    async handleRequest(method, endpoint, data = {}, clientId = 'demo-client') {
        this.logger.info(`${method} ${endpoint}`);

        try {
            switch (endpoint) {
                case '/users':
                    if (method === 'POST') {
                        return await this.userAPI.createUser(data, clientId);
                    } else if (method === 'GET') {
                        return await this.userAPI.listUsers();
                    }
                    break;

                case '/users/:id':
                    if (method === 'GET') {
                        return await this.userAPI.getUser(data.id);
                    }
                    break;

                case '/posts':
                    if (method === 'POST') {
                        return await this.postAPI.createPost(data, clientId);
                    } else if (method === 'GET') {
                        return await this.postAPI.listPosts(data.published);
                    }
                    break;

                case '/posts/:id':
                    if (method === 'GET') {
                        return await this.postAPI.getPost(data.id);
                    }
                    break;

                case '/users/:id/posts':
                    if (method === 'GET') {
                        return await this.postAPI.getUserPosts(data.id);
                    }
                    break;

                case '/stats':
                    if (method === 'GET') {
                        const stats = this.database.getStats();
                        return { status: 'success', timestamp: new Date().toISOString(), data: stats };
                    }
                    break;

                default:
                    return {
                        status: 'error',
                        timestamp: new Date().toISOString(),
                        data: { error: 'Endpoint not found', code: 'not_found' }
                    };
            }
        } catch (error) {
            this.logger.error(`Request failed: ${error.message}`);
            return {
                status: 'error',
                timestamp: new Date().toISOString(),
                data: { error: 'Internal server error', code: 'internal_error' }
            };
        }
    }
}

