// Data models for our Library Management System
import java.time.LocalDate
import java.time.format.DateTimeFormatter

// Data class for Book
data class Book(
    val isbn: String,
    val title: String,
    val author: String,
    val publishedDate: LocalDate,
    val genre: Genre,
    val availableCopies: Int = 1
) {
    companion object {
        fun fromString(data: String): Book? {
            val parts = data.split("|")
            return if (parts.size == 6) {
                Book(
                    isbn = parts[0],
                    title = parts[1],
                    author = parts[2],
                    publishedDate = LocalDate.parse(parts[3]),
                    genre = Genre.valueOf(parts[4]),
                    availableCopies = parts[5].toInt()
                )
            } else null
        }
    }

    fun toFormattedString(): String = 
        "üìö $title by $author (ISBN: $isbn)\n" +
        "   Genre: $genre | Published: ${publishedDate.format(DateTimeFormatter.ISO_LOCAL_DATE)}"
}

// Data class for Member
data class Member(
    val id: Int,
    val name: String,
    val email: String,
    val membershipType: MembershipType,
    val joinDate: LocalDate = LocalDate.now()
) {
    fun getMaxBorrowLimit(): Int = when (membershipType) {
        is MembershipType.Regular -> membershipType.borrowLimit
        is MembershipType.Premium -> membershipType.borrowLimit
        is MembershipType.Student -> membershipType.borrowLimit
    }
}

// Data class for Loan
data class Loan(
    val id: Int,
    val memberId: Int,
    val bookIsbn: String,
    val loanDate: LocalDate = LocalDate.now(),
    val dueDate: LocalDate = loanDate.plusDays(14),
    val returnDate: LocalDate? = null
) {
    val isOverdue: Boolean
        get() = returnDate == null && LocalDate.now().isAfter(dueDate)

    val daysOverdue: Long
        get() = if (isOverdue) {
            LocalDate.now().toEpochDay() - dueDate.toEpochDay()
        } else 0
}

// Enum for book genres
enum class Genre {
    FICTION, NON_FICTION, SCIENCE, HISTORY, BIOGRAPHY, 
    MYSTERY, ROMANCE, THRILLER, FANTASY, TECHNICAL
}

// Sealed class for membership types
sealed class MembershipType(val borrowLimit: Int) {
    object Regular : MembershipType(3)
    object Premium : MembershipType(10)
    object Student : MembershipType(5)

    fun describe(): String = when (this) {
        is Regular -> "Regular membership (up to $borrowLimit books)"
        is Premium -> "Premium membership (up to $borrowLimit books)"
        is Student -> "Student membership (up to $borrowLimit books)"
    }
}

// Sealed class for operation results
sealed class OperationResult<out T> {
    data class Success<T>(val data: T) : OperationResult<T>()
    data class Error(val message: String) : OperationResult<Nothing>()

    inline fun onSuccess(action: (T) -> Unit): OperationResult<T> {
        if (this is Success) action(data)
        return this
    }

    inline fun onError(action: (String) -> Unit): OperationResult<T> {
        if (this is Error) action(message)
        return this
    }
}

