// Extension functions and utilities for Library Management System
import java.time.LocalDate
import java.time.format.DateTimeFormatter

// Extension function for String to validate ISBN
fun String.isValidIsbn(): Boolean {
    val cleanIsbn = this.replace("-", "").replace(" ", "")
    return when (cleanIsbn.length) {
        10 -> cleanIsbn.all { it.isDigit() || it == 'X' }
        13 -> cleanIsbn.all { it.isDigit() }
        else -> false
    }
}

// Extension function for String to validate email
fun String.isValidEmail(): Boolean = 
    this.matches(Regex("^[A-Za-z0-9+_.-]+@[A-Za-z0-9.-]+\\.[A-Za-z]{2,}$"))

// Extension function for LocalDate formatting
fun LocalDate.toReadableString(): String = 
    this.format(DateTimeFormatter.ofPattern("MMM dd, yyyy"))

// Extension function for List of Books
fun List<Book>.filterByGenre(genre: Genre): List<Book> = 
    this.filter { it.genre == genre }

fun List<Book>.filterByAuthor(author: String): List<Book> = 
    this.filter { it.author.contains(author, ignoreCase = true) }

fun List<Book>.sortByTitle(): List<Book> = 
    this.sortedBy { it.title }

fun List<Book>.groupByGenre(): Map<Genre, List<Book>> = 
    this.groupBy { it.genre }

// Extension function for List of Loans
fun List<Loan>.getActiveLoans(): List<Loan> = 
    this.filter { it.returnDate == null }

fun List<Loan>.getOverdueLoans(): List<Loan> = 
    this.filter { it.isOverdue }

// Utility functions using lambda expressions
fun <T> measureTimeMillis(block: () -> T): Pair<T, Long> {
    val start = System.currentTimeMillis()
    val result = block()
    val time = System.currentTimeMillis() - start
    return result to time
}

// Higher-order function for retry logic
fun <T> retry(times: Int = 3, delay: Long = 1000, block: () -> T): T? {
    repeat(times - 1) {
        try {
            return block()
        } catch (e: Exception) {
            Thread.sleep(delay)
        }
    }
    return try {
        block()
    } catch (e: Exception) {
        null
    }
}

// Infix function for creating date ranges
infix fun LocalDate.until(other: LocalDate): List<LocalDate> {
    val dates = mutableListOf<LocalDate>()
    var current = this
    while (!current.isAfter(other)) {
        dates.add(current)
        current = current.plusDays(1)
    }
    return dates
}

// DSL-style builder for creating books
class BookBuilder {
    var isbn: String = ""
    var title: String = ""
    var author: String = ""
    var publishedDate: LocalDate = LocalDate.now()
    var genre: Genre = Genre.FICTION
    var availableCopies: Int = 1

    fun build(): Book = Book(isbn, title, author, publishedDate, genre, availableCopies)
}

fun book(init: BookBuilder.() -> Unit): Book {
    val builder = BookBuilder()
    builder.init()
    return builder.build()
}

