// Service layer for Library Management System
import java.time.LocalDate
import kotlin.random.Random

// Library service with coroutine-like concepts (simulated)
class LibraryService {
    private val books = mutableListOf<Book>()
    private val members = mutableListOf<Member>()
    private val loans = mutableListOf<Loan>()
    private var nextLoanId = 1
    private var nextMemberId = 1

    // Book management
    fun addBook(book: Book): OperationResult<Book> {
        return if (book.isbn.isValidIsbn()) {
            books.add(book)
            OperationResult.Success(book)
        } else {
            OperationResult.Error("Invalid ISBN format")
        }
    }

    fun findBookByIsbn(isbn: String): Book? = 
        books.find { it.isbn == isbn }

    fun searchBooks(query: String): List<Book> = 
        books.filter { book ->
            book.title.contains(query, ignoreCase = true) ||
            book.author.contains(query, ignoreCase = true)
        }

    // Member management
    fun registerMember(
        name: String, 
        email: String, 
        type: MembershipType = MembershipType.Regular
    ): OperationResult<Member> {
        return if (email.isValidEmail()) {
            val member = Member(nextMemberId++, name, email, type)
            members.add(member)
            OperationResult.Success(member)
        } else {
            OperationResult.Error("Invalid email format")
        }
    }

    fun findMemberById(id: Int): Member? = 
        members.find { it.id == id }

    // Loan management
    fun borrowBook(memberId: Int, isbn: String): OperationResult<Loan> {
        val member = findMemberById(memberId) 
            ?: return OperationResult.Error("Member not found")

        val book = findBookByIsbn(isbn) 
            ?: return OperationResult.Error("Book not found")

        val activeLoans = loans.getActiveLoans().count { it.memberId == memberId }

        return when {
            activeLoans >= member.getMaxBorrowLimit() -> 
                OperationResult.Error("Member has reached borrowing limit")
            book.availableCopies <= 0 -> 
                OperationResult.Error("No copies available")
            else -> {
                val loan = Loan(nextLoanId++, memberId, isbn)
                loans.add(loan)
                // Update available copies (in real app, this would be atomic)
                val updatedBook = book.copy(availableCopies = book.availableCopies - 1)
                books[books.indexOf(book)] = updatedBook
                OperationResult.Success(loan)
            }
        }
    }

    fun returnBook(loanId: Int): OperationResult<Loan> {
        val loan = loans.find { it.id == loanId && it.returnDate == null }
            ?: return OperationResult.Error("Active loan not found")

        val updatedLoan = loan.copy(returnDate = LocalDate.now())
        loans[loans.indexOf(loan)] = updatedLoan

        // Update available copies
        findBookByIsbn(loan.bookIsbn)?.let { book ->
            val updatedBook = book.copy(availableCopies = book.availableCopies + 1)
            books[books.indexOf(book)] = updatedBook
        }

        return OperationResult.Success(updatedLoan)
    }

    // Analytics using collection operations
    fun getLibraryStats(): LibraryStats {
        val totalBooks = books.size
        val totalMembers = members.size
        val activeLoans = loans.getActiveLoans().size
        val overdueLoans = loans.getOverdueLoans().size
        val booksByGenre = books.groupByGenre().mapValues { it.value.size }
        val mostPopularGenre = booksByGenre.maxByOrNull { it.value }?.key

        return LibraryStats(
            totalBooks = totalBooks,
            totalMembers = totalMembers,
            activeLoans = activeLoans,
            overdueLoans = overdueLoans,
            booksByGenre = booksByGenre,
            mostPopularGenre = mostPopularGenre
        )
    }

    // Recommendation engine using when expressions and lambdas
    fun recommendBooks(memberId: Int): List<Book> {
        val memberLoans = loans.filter { it.memberId == memberId }
        val borrowedGenres = memberLoans.mapNotNull { loan ->
            findBookByIsbn(loan.bookIsbn)?.genre
        }.distinct()

        return books.filter { book ->
            when {
                borrowedGenres.isEmpty() -> true // New member, show all
                book.genre in borrowedGenres -> Random.nextDouble() > 0.3
                else -> Random.nextDouble() > 0.7
            }
        }.take(5)
    }

    // Batch operations
    fun processOverdueNotifications(): List<OverdueNotification> {
        return loans.getOverdueLoans().mapNotNull { loan ->
            val member = findMemberById(loan.memberId)
            val book = findBookByIsbn(loan.bookIsbn)

            if (member != null && book != null) {
                OverdueNotification(
                    memberName = member.name,
                    memberEmail = member.email,
                    bookTitle = book.title,
                    daysOverdue = loan.daysOverdue,
                    fine = calculateFine(loan.daysOverdue)
                )
            } else null
        }
    }

    private fun calculateFine(daysOverdue: Long): Double = 
        daysOverdue * 0.50 // $0.50 per day

    // Getters for testing
    fun getAllBooks() = books.toList()
    fun getAllMembers() = members.toList()
    fun getAllLoans() = loans.toList()
}

// Data classes for analytics
data class LibraryStats(
    val totalBooks: Int,
    val totalMembers: Int,
    val activeLoans: Int,
    val overdueLoans: Int,
    val booksByGenre: Map<Genre, Int>,
    val mostPopularGenre: Genre?
)

data class OverdueNotification(
    val memberName: String,
    val memberEmail: String,
    val bookTitle: String,
    val daysOverdue: Long,
    val fine: Double
) {
    fun formatMessage(): String = """
        Dear $memberName,

        Your loan for "$bookTitle" is $daysOverdue days overdue.
        Current fine: $${
"%.2f".format(fine)}

        Please return the book as soon as possible.

        Thank you,
        Library Management
    """.trimIndent()
}

