# Data models for our Python web application
from dataclasses import dataclass
from typing import List, Optional
import json

@dataclass
class User:
    id: int
    username: str
    email: str
    active: bool = True

    def to_dict(self):
        return {
            'id': self.id,
            'username': self.username,
            'email': self.email,
            'active': self.active
        }

    @classmethod
    def from_dict(cls, data):
        return cls(**data)

@dataclass
class Post:
    id: int
    title: str
    content: str
    author_id: int
    published: bool = False

    def to_dict(self):
        return {
            'id': self.id,
            'title': self.title,
            'content': self.content,
            'author_id': self.author_id,
            'published': self.published
        }

class Database:
    def __init__(self):
        self.users = {}
        self.posts = {}
        self.next_user_id = 1
        self.next_post_id = 1

    def create_user(self, username: str, email: str) -> User:
        user = User(self.next_user_id, username, email)
        self.users[user.id] = user
        self.next_user_id += 1
        return user

    def get_user(self, user_id: int) -> Optional[User]:
        return self.users.get(user_id)

    def create_post(self, title: str, content: str, author_id: int) -> Post:
        post = Post(self.next_post_id, title, content, author_id)
        self.posts[post.id] = post
        self.next_post_id += 1
        return post

    def get_posts_by_author(self, author_id: int) -> List[Post]:
        return [post for post in self.posts.values() if post.author_id == author_id]

