# Utility functions for our Python web application
import json
import hashlib
from typing import Dict, Any
from datetime import datetime

def hash_password(password: str) -> str:
    """Simple password hashing (not for production!)"""
    return hashlib.sha256(password.encode()).hexdigest()

def verify_password(password: str, hashed: str) -> bool:
    """Verify a password against its hash"""
    return hash_password(password) == hashed

def format_timestamp(dt: datetime = None) -> str:
    """Format a datetime as ISO string"""
    if dt is None:
        dt = datetime.now()
    return dt.isoformat()

def validate_email(email: str) -> bool:
    """Basic email validation"""
    return '@' in email and '.' in email.split('@')[1]

def sanitize_input(text: str, max_length: int = 1000) -> str:
    """Basic input sanitization"""
    return text.strip()[:max_length]

def json_response(data: Dict[str, Any], status: str = 'success') -> str:
    """Create a JSON response"""
    response = {
        'status': status,
        'timestamp': format_timestamp(),
        'data': data
    }
    return json.dumps(response, indent=2)

def error_response(message: str, code: str = 'error') -> str:
    """Create an error JSON response"""
    return json_response({'error': message, 'code': code}, status='error')

class Logger:
    def __init__(self, name: str):
        self.name = name

    def info(self, message: str):
        print(f"[{format_timestamp()}] {self.name} INFO: {message}")

    def error(self, message: str):
        print(f"[{format_timestamp()}] {self.name} ERROR: {message}")

    def debug(self, message: str):
        print(f"[{format_timestamp()}] {self.name} DEBUG: {message}")

