# API handlers for our Python web application
from models import Database, User, Post
from utils import validate_email, sanitize_input, json_response, error_response, Logger
from typing import Dict, Any

class UserAPI:
    def __init__(self, db: Database):
        self.db = db
        self.logger = Logger('UserAPI')

    def create_user(self, data: Dict[str, Any]) -> str:
        """Create a new user"""
        try:
            username = sanitize_input(data.get('username', ''))
            email = sanitize_input(data.get('email', ''))

            if not username or not email:
                return error_response('Username and email are required')

            if not validate_email(email):
                return error_response('Invalid email format')

            user = self.db.create_user(username, email)
            self.logger.info(f'Created user: {username}')

            return json_response(user.to_dict())

        except Exception as e:
            self.logger.error(f'Failed to create user: {e}')
            return error_response('Failed to create user')

    def get_user(self, user_id: int) -> str:
        """Get user by ID"""
        user = self.db.get_user(user_id)
        if user:
            return json_response(user.to_dict())
        return error_response('User not found', 'not_found')

class PostAPI:
    def __init__(self, db: Database):
        self.db = db
        self.logger = Logger('PostAPI')

    def create_post(self, data: Dict[str, Any]) -> str:
        """Create a new post"""
        try:
            title = sanitize_input(data.get('title', ''))
            content = sanitize_input(data.get('content', ''), max_length=5000)
            author_id = data.get('author_id')

            if not title or not content or not author_id:
                return error_response('Title, content, and author_id are required')

            # Verify author exists
            if not self.db.get_user(author_id):
                return error_response('Author not found')

            post = self.db.create_post(title, content, author_id)
            self.logger.info(f'Created post: {title}')

            return json_response(post.to_dict())

        except Exception as e:
            self.logger.error(f'Failed to create post: {e}')
            return error_response('Failed to create post')

    def get_user_posts(self, author_id: int) -> str:
        """Get all posts by a user"""
        posts = self.db.get_posts_by_author(author_id)
        posts_data = [post.to_dict() for post in posts]
        return json_response({'posts': posts_data, 'count': len(posts_data)})

