// Utility functions for our TypeScript task management application

import { Task, TaskStatus, TaskPriority, User, ApiResponse } from './types';

export function generateId(): string {
  return `${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
}

export function validateEmail(email: string): boolean {
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email);
}

export function sanitizeString(input: string, maxLength: number = 1000): string {
  return input.trim().slice(0, maxLength);
}

export function formatDate(date: Date): string {
  return date.toLocaleDateString('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric'
  });
}

export function parseDate(dateString: string): Date | null {
  const date = new Date(dateString);
  return isNaN(date.getTime()) ? null : date;
}

export function isTaskOverdue(task: Task): boolean {
  if (!task.dueDate || task.status === TaskStatus.Done) {
    return false;
  }
  return new Date(task.dueDate) < new Date();
}

export function sortTasksByPriority(tasks: Task[]): Task[] {
  const priorityOrder = {
    [TaskPriority.Urgent]: 0,
    [TaskPriority.High]: 1,
    [TaskPriority.Medium]: 2,
    [TaskPriority.Low]: 3
  };

  return [...tasks].sort((a, b) => {
    return priorityOrder[a.priority] - priorityOrder[b.priority];
  });
}

export function groupTasksByStatus(tasks: Task[]): Map<TaskStatus, Task[]> {
  const grouped = new Map<TaskStatus, Task[]>();

  for (const status of Object.values(TaskStatus)) {
    grouped.set(status, []);
  }

  tasks.forEach(task => {
    const statusTasks = grouped.get(task.status) || [];
    statusTasks.push(task);
    grouped.set(task.status, statusTasks);
  });

  return grouped;
}

export function createApiResponse<T>(data?: T, error?: string): ApiResponse<T> {
  return {
    success: !error,
    data,
    error,
    timestamp: new Date().toISOString()
  };
}

export class Logger {
  constructor(private readonly context: string) {}

  info(message: string, data?: any): void {
    console.log(`[${new Date().toISOString()}] [${this.context}] INFO:`, message, data || '');
  }

  error(message: string, error?: any): void {
    console.error(`[${new Date().toISOString()}] [${this.context}] ERROR:`, message, error || '');
  }

  debug(message: string, data?: any): void {
    console.log(`[${new Date().toISOString()}] [${this.context}] DEBUG:`, message, data || '');
  }
}

export function hasPermission(user: User, action: string): boolean {
  const permissions = {
    admin: ['create', 'read', 'update', 'delete', 'assign'],
    manager: ['create', 'read', 'update', 'assign'],
    member: ['create', 'read', 'update']
  };

  return permissions[user.role]?.includes(action) || false;
}

