// Service classes for our TypeScript task management application

import {
  Task, User, TaskStatus, TaskPriority, TaskFilter,
  CreateTaskDto, UpdateTaskDto, ApiResponse, TaskStats, UserRole
} from './types';
import {
  generateId, validateEmail, sanitizeString, parseDate,
  isTaskOverdue, sortTasksByPriority, createApiResponse,
  Logger, hasPermission
} from './utils';

export class UserService {
  private users: Map<string, User> = new Map();
  private logger = new Logger('UserService');

  createUser(username: string, email: string, role: UserRole = UserRole.Member): ApiResponse<User> {
    try {
      if (!username || !email) {
        return createApiResponse(undefined, 'Username and email are required');
      }

      if (!validateEmail(email)) {
        return createApiResponse(undefined, 'Invalid email format');
      }

      const user: User = {
        id: generateId(),
        username: sanitizeString(username, 50),
        email: sanitizeString(email, 100),
        role,
        createdAt: new Date(),
        active: true
      };

      this.users.set(user.id, user);
      this.logger.info(`User created: ${user.username}`);

      return createApiResponse(user);
    } catch (error) {
      this.logger.error('Failed to create user', error);
      return createApiResponse(undefined, 'Failed to create user');
    }
  }

  getUser(userId: string): User | undefined {
    return this.users.get(userId);
  }

  getAllUsers(): User[] {
    return Array.from(this.users.values());
  }

  updateUserRole(userId: string, newRole: UserRole): ApiResponse<User> {
    const user = this.users.get(userId);
    if (!user) {
      return createApiResponse(undefined, 'User not found');
    }

    user.role = newRole;
    this.logger.info(`Updated role for user ${user.username} to ${newRole}`);

    return createApiResponse(user);
  }
}

export class TaskService {
  private tasks: Map<string, Task> = new Map();
  private logger = new Logger('TaskService');

  constructor(private userService: UserService) {}

  createTask(dto: CreateTaskDto, createdBy: string): ApiResponse<Task> {
    try {
      // Validate required fields
      if (!dto.title || !dto.description || !dto.assigneeId) {
        return createApiResponse(undefined, 'Title, description, and assignee are required');
      }

      // Validate assignee exists
      const assignee = this.userService.getUser(dto.assigneeId);
      if (!assignee) {
        return createApiResponse(undefined, 'Assignee not found');
      }

      // Parse due date if provided
      let dueDate: Date | undefined;
      if (dto.dueDate) {
        const parsed = parseDate(dto.dueDate);
        if (!parsed) {
          return createApiResponse(undefined, 'Invalid due date format');
        }
        dueDate = parsed;
      }

      const task: Task = {
        id: generateId(),
        title: sanitizeString(dto.title, 200),
        description: sanitizeString(dto.description, 2000),
        status: TaskStatus.Todo,
        priority: dto.priority,
        assigneeId: dto.assigneeId,
        createdBy,
        dueDate,
        tags: dto.tags?.map(tag => sanitizeString(tag, 50)) || [],
        createdAt: new Date(),
        updatedAt: new Date()
      };

      this.tasks.set(task.id, task);
      this.logger.info(`Task created: ${task.title}`);

      return createApiResponse(task);
    } catch (error) {
      this.logger.error('Failed to create task', error);
      return createApiResponse(undefined, 'Failed to create task');
    }
  }

  updateTask(taskId: string, dto: UpdateTaskDto, userId: string): ApiResponse<Task> {
    const task = this.tasks.get(taskId);
    if (!task) {
      return createApiResponse(undefined, 'Task not found');
    }

    const user = this.userService.getUser(userId);
    if (!user) {
      return createApiResponse(undefined, 'User not found');
    }

    // Check permissions
    if (task.assigneeId !== userId && !hasPermission(user, 'assign')) {
      return createApiResponse(undefined, 'Insufficient permissions');
    }

    // Update fields
    if (dto.title !== undefined) task.title = sanitizeString(dto.title, 200);
    if (dto.description !== undefined) task.description = sanitizeString(dto.description, 2000);
    if (dto.status !== undefined) task.status = dto.status;
    if (dto.priority !== undefined) task.priority = dto.priority;
    if (dto.assigneeId !== undefined) {
      const newAssignee = this.userService.getUser(dto.assigneeId);
      if (!newAssignee) {
        return createApiResponse(undefined, 'New assignee not found');
      }
      task.assigneeId = dto.assigneeId;
    }
    if (dto.dueDate !== undefined) {
      const parsed = parseDate(dto.dueDate);
      if (!parsed) {
        return createApiResponse(undefined, 'Invalid due date format');
      }
      task.dueDate = parsed;
    }
    if (dto.tags !== undefined) {
      task.tags = dto.tags.map(tag => sanitizeString(tag, 50));
    }

    task.updatedAt = new Date();
    this.logger.info(`Task updated: ${task.title}`);

    return createApiResponse(task);
  }

  getTasks(filter?: TaskFilter): Task[] {
    let tasks = Array.from(this.tasks.values());

    if (filter) {
      if (filter.status) {
        tasks = tasks.filter(t => t.status === filter.status);
      }
      if (filter.priority) {
        tasks = tasks.filter(t => t.priority === filter.priority);
      }
      if (filter.assigneeId) {
        tasks = tasks.filter(t => t.assigneeId === filter.assigneeId);
      }
      if (filter.tags && filter.tags.length > 0) {
        tasks = tasks.filter(t => 
          filter.tags!.some(tag => t.tags.includes(tag))
        );
      }
    }

    return sortTasksByPriority(tasks);
  }

  getTaskStats(): TaskStats {
    const tasks = Array.from(this.tasks.values());

    const stats: TaskStats = {
      total: tasks.length,
      byStatus: {
        [TaskStatus.Todo]: 0,
        [TaskStatus.InProgress]: 0,
        [TaskStatus.Review]: 0,
        [TaskStatus.Done]: 0
      },
      byPriority: {
        [TaskPriority.Low]: 0,
        [TaskPriority.Medium]: 0,
        [TaskPriority.High]: 0,
        [TaskPriority.Urgent]: 0
      },
      overdue: 0
    };

    tasks.forEach(task => {
      stats.byStatus[task.status]++;
      stats.byPriority[task.priority]++;
      if (isTaskOverdue(task)) {
        stats.overdue++;
      }
    });

    return stats;
  }

  deleteTask(taskId: string, userId: string): ApiResponse<void> {
    const task = this.tasks.get(taskId);
    if (!task) {
      return createApiResponse(undefined, 'Task not found');
    }

    const user = this.userService.getUser(userId);
    if (!user || !hasPermission(user, 'delete')) {
      return createApiResponse(undefined, 'Insufficient permissions');
    }

    this.tasks.delete(taskId);
    this.logger.info(`Task deleted: ${task.title}`);

    return createApiResponse();
  }
}

